function smoothScroll(t){$('a[href^="#"]').on("click",function(o){var l=$($(this).attr("href"));l.length&&(o.preventDefault(),$("html, body").animate({scrollTop:l.offset().top},t))})}function workBelt(){$(".thumb-unit").click(function(){$(".work-belt").css("left","-100%")}),$(".work-return").click(function(){$(".work-belt").css("left","0%")})}$(function(){smoothScroll(300),workBelt()});